import logo from './logo.svg';
import './App.css';
import { useState, useEffect, Checkbox, FormGroup, FormControlLabel } from "react";
import * as React from 'react';
import bakeryData from "./assets/bakery-data.json";
import BakeryItem from "./components/BakeryItem.js";
import cloneDeep from 'lodash/cloneDeep';


function App() {
    const [options, setOptions] = useState(bakeryData)
    const [filters, setFilters] = useState([])
    const [favs, setFaves] = useState([])
    let flag
    let initialState = []

    function mySort(event, name){
      let copy = cloneDeep(bakeryData)
      let touse;
      if (name == "Popularity"){
        touse = bakeryData
      }
      if (name == "Price"){  
        touse = copy.sort((a, b) => a.price > b.price ? 1 : -1).reverse()
      }
      if (name == "Calories"){
        touse = copy.sort((a, b) => a.cals > b.cals ? 1 : -1).reverse()
      }
      setOptions(touse);
    }

    function addFilter(name) {
      if (!filters.includes(name)) {
        setFilters(filters => [...filters, name])
      }
    }

    function removeFilter(name) {
      setFilters(filters => filters.filter(obj => {
          return obj !== name
        }))
    }

    useEffect(() => { 
      if(filters !== initialState){
        let copy = cloneDeep(bakeryData)
        filters.forEach(x => copy = copy.filter(a => a.type.includes(x)))
        // filters.forEach(x => copy = copy.filter(a => a.dietary.includes(x)))

        setOptions(copy);
        initialState = filters
      }
    }, [filters])
    function myfilter(event, name){      
      // console.log(filters, name)
      if (filters.includes(name)){
        removeFilter(name)
      } else {
        addFilter(name)

      }

      // let copy = cloneDeep(bakeryData)
      // console.log(filters)
      // filters.forEach(x => copy = copy.filter(a => a.type.includes(x)))

      // touse = copy.filter(a => a.type.includes(name))
      // if (touse.length == 0){
      //   flag = false
      // }
      // console.log(touse, copy, name)
      // setOptions(copy);
    }

    function myfilterDiet(event, name){
      let copy = cloneDeep(bakeryData)
      let touse;
      touse = copy.filter(a => a.dietary.includes(name))
      if (touse.length == 0){
        flag = false
      }
      setOptions(touse);
    }

    function myFunctionAdd(event, item){
      if (!favs.includes(item)){
        setFaves(favs => [...favs, item]);
      }
    }

    function myFunctionDel(event, item){
      if (options === favs && favs.length === 1) {
        setFaves([])
        setOptions([])
      }
      else {
        setFaves(favs => favs.filter(obj => {
          return obj.name !== item.name
        }))
      }
    }

    function myFavorites(){
      setOptions(favs);
    }


    function Button({ name, onClick }) {
      return (
        <button onClick={event => mySort(event, name)}>
          <p>{name}</p>
        </button>
      );
    }

    function ButtonType({ name, onClick }) {
      return (
        <button onClick={event => myfilter(event, name)}>
          <p>{name}</p>
        </button>
      );
    }

    function ButtonDiet({ name, onClick }) {
      return (
        <button onClick={event => myfilterDiet(event, name)}>
          <p>{name}</p>
        </button>
      );
    }
    function ButtonFavs({ name, onClick }) {
      return (
        <button onClick={myFavorites}>
          <p>{name}</p>
        </button>
      );
    }

    function ButtonFav({ item, onClick }) {
      if (favs.includes(item)) {
        return (
          <button onClick={event => myFunctionDel(event, item)}>
            <p>Remove From Favorites</p>
          </button>
        );
      } else {
        return (
          <button onClick={event => myFunctionAdd(event, item)}>
            <p>Add to Favorites</p>
          </button>
        );
      }
    }

    function isFav({item}) {
      if (favs.includes(item)) {
        return (
          <button onClick={event => myFunctionDel(event, item)}>
            <p>Remove From Favorites</p>
          </button>
        );
      } else {
        return (
          <button onClick={event => myFunctionAdd(event, item)}>
            <p>Add to Favorites</p>
          </button>
        );
      }
    }

  // let content
  // if (options.length == 0 && !flag) {
  //   content = bakeryData.map(item => (
  //     <div className="Item">
  //         <div className="Deets">
  //           <BakeryItem item={item}></BakeryItem>
  //         </div>
  //           <isFav item={item}/>
  //         {/* <ButtonFav item={item} onClick={myFunctionAdd}/> */}
  //       </div>
  //       // <div className="Menu">
  //       //   <BakeryItem item={item}></BakeryItem>
  //       //   <MyButton item={item} onClick={myFunction}/>
  //       // </div>
  //     ))
  // }

  return (
    <div className="App">
      <div className="Header">
        <h1>The Walking Bakery</h1>{" "}
      </div>
      <div className="Body">

      <div className="Cart">
        <div className="CartHeader">
            <h2>Filter Options</h2>
             {/* <p className="Total"></p> */}
          </div>
        <h4>Sort By</h4>
          <Button name="Popularity" onClick={mySort}/>
          <Button name="Price" onClick={mySort}/>
          <Button name="Calories" onClick={mySort}/>
        <h4>Type</h4>
          {/* <label>
            <input type="checkbox"
            // checked={checked}

            // checked={options => myfilter()}
            onChange={event => myfilter(event, "Pastry")}
            />
            <ButtonType name="Pastry"/> */}
            {/* Pastry */}
          {/* </label> */}
          <ButtonType name="Pastry" onClick={myfilter}/>
          {/* <label>
            <input type="checkbox"/>
            <ButtonType name="Bread" onClick={myfilter}/>
          </label> */}
          <ButtonType name="Bread" onClick={myfilter}/>
          <ButtonType name="Cake" onClick={myfilter}/>
        <h4>Dietary</h4>
          <ButtonDiet name="Gluten-Free" onClick={myfilterDiet}/>
          <ButtonDiet name="Dairy-Free" onClick={myfilterDiet}/>
          <ButtonDiet name="Nut-Free" onClick={myfilterDiet}/>
        <h4>Other</h4>
          <ButtonFavs name="Favorite" onClick={myFavorites}/>
        </div>
        {/* <h1>The Bakery</h1> */}
        {/* {bakeryData.map((item, index) => {
          <div>
            <BakeryItem item={item}></BakeryItem>
            <MyButton item={item} onClick={myFunction}/>
          </div>
        })} */}
        {/* <div className="Menu">
        {content}
        </div> */}

        <div className="Menu">
          {options.map((item, index) => (
            <div className="Item">
              <div className="Deets">
                <BakeryItem item={item}></BakeryItem>
              </div>
                <ButtonFav item={item} onClick={isFav}/>
            </div>

            // <div className="Menu">
            //   <BakeryItem item={item}></BakeryItem>
            //   <ButtonFav item={item} onClick={myFunctionAdd}/>
            // </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
