import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from "react";
import bakeryData from "./assets/bakery-data.json";
import BakeryItem from "./components/BakeryItem.js";
import cloneDeep from 'lodash/cloneDeep';


function App() {
  // let inital = 
    const [options, setOptions] = useState(bakeryData)

    function myFunction(event, name, price){
    // console.log(variantInfo)
    setOptions(name);
    // handleTotal(price);
    }
    function MyButton({ item, onClick }) {
    return (
      <button onClick={event => myFunction(event, item.name, item.price)}>
        <p>Add to Cart</p>
      </button>
    );
  }
    function mySort(event, name){
      let copy = cloneDeep(bakeryData)
      let touse;
      if (name == "Popularity"){
        touse = bakeryData
      }
      if (name == "Price"){

        touse = copy.sort((a, b) => a.price > b.price ? 1 : -1)

      }
      if (name == "Calories"){
        touse = copy.sort((a, b) => a.cals > b.cals ? 1 : -1)

      }
      setOptions(touse);
    }

    function myfilter(event, name){
      let copy = cloneDeep(bakeryData)
      let touse;
      if (name == "Pastry"){
        touse = bakeryData
      }
      if (name == "Bread"){

        touse = copy.filter(a => a.type == "Bread" ? a : -1)

      }
      if (name == "Cake"){
        touse = copy.sort((a, b) => a.cals > b.cals ? 1 : -1)

      }
      setOptions(touse);
    }


       function Button({ name, onClick }) {
    // console.log(item.price)
    return (
      <button onClick={event => mySort(event, name)}>
        <p>{name}</p>
      </button>
    );
  }

  let content
  if (options.length == 0) {
    content = bakeryData.map(item => (
        <div>
          <BakeryItem item={item}></BakeryItem>
          <MyButton item={item} onClick={myFunction}/>
        </div>
      ))
  }


  console.log(options)
  return (
    <div className="App">
      <div className="App">
        <h4>Sort By</h4>
          <Button name="Popularity" onClick={mySort}/>
          <Button name="Price" onClick={mySort}/>
          <Button name="Calories" onClick={mySort}/>
        <h4>Type</h4>
          <Button name="Pastry" onClick={myfilter}/>
          <Button name="Bread" onClick={myfilter}/>
          <Button name="Cake" onClick={myfilter}/>
        <h4>Dietary</h4>
          <Button name="Gluten-Free" onClick={mySort}/>
          <Button name="Dairy-free" onClick={mySort}/>
          <Button name="Nut-Free" onClick={mySort}/>
        <h4>Other</h4>
          <Button name="Favority" onClick={mySort}/>
      </div>
      <h1>The Bakery</h1>
      {/* {bakeryData.map((item, index) => {
        <div>
          <BakeryItem item={item}></BakeryItem>
          <MyButton item={item} onClick={myFunction}/>
        </div>
      })} */}
      {content}

      {options.map((item, index) => (
        <div>
          <BakeryItem item={item}></BakeryItem>
          <MyButton item={item} onClick={myFunction}/>
        </div>
      ))}
    </div>
  );
}

export default App;
