import logo from './logo.svg';
import './App.css';
import { useState, useEffect, Checkbox, FormGroup, FormControlLabel } from "react";
import * as React from 'react';
import bakeryData from "./assets/bakery-data.json";
import BakeryItem from "./components/BakeryItem.js";
import cloneDeep from 'lodash/cloneDeep';


function App() {
  // let inital = 
    const [options, setOptions] = useState(bakeryData)
    const [favs, setFaves] = useState([])
    let flag = true;

    function myFunction(event, name, price){
    // console.log(variantInfo)
    setOptions(name);
    // handleTotal(price);
    }
    function MyButton({ item, onClick }) {
    return (
      <button onClick={event => myFunction(event, item.name, item.price)}>
        <p>Add to Cart</p>
      </button>
    );
  }
    function mySort(event, name){
      let copy = cloneDeep(bakeryData)
      let touse;
      if (name == "Popularity"){
        touse = bakeryData
      }
      if (name == "Price"){

        touse = copy.sort((a, b) => a.price > b.price ? 1 : -1)

      }
      if (name == "Calories"){
        touse = copy.sort((a, b) => a.cals > b.cals ? 1 : -1)

      }
      setOptions(touse);
    }

    function myfilter(event, name){
      let copy = cloneDeep(bakeryData)
      let touse;
      touse = copy.filter(a => a.type.includes(name))
      if (touse.length == 0){
        flag = false
      }
      setOptions(touse);
    }

    function myfilterDiet(event, name){
      let copy = cloneDeep(bakeryData)
      let touse;
      touse = copy.filter(a => a.dietary.includes(name))
      if (touse.length == 0){
        flag = false
      }
      setOptions(touse);
    }

    function myFunctionAdd(event, item){
      if (!favs.includes(item)){
        setFaves(favs => [...favs, item]);
      }
    }
    function myFavorites(){
      setOptions(favs);
    }


       function Button({ name, onClick }) {
    // console.log(item.price)
    return (
      <button onClick={event => mySort(event, name)}>
        <p>{name}</p>
      </button>
    );
  }

         function ButtonType({ name, onClick }) {
    // console.log(item.price)
    return (
      <button onClick={event => myfilter(event, name)}>
        <p>{name}</p>
      </button>
    );
  }

          function ButtonDiet({ name, onClick }) {
    // console.log(item.price)
    return (
      <button onClick={event => myfilterDiet(event, name)}>
        <p>{name}</p>
      </button>
    );
  }
    function ButtonFavs({ name, onClick }) {
    return (
      <button onClick={myFavorites}>
        <p>{name}</p>
      </button>
    );
  }

    function ButtonFav({ item, onClick }) {
    return (
      <button onClick={event => myFunctionAdd(event, item)}>
        <p>Add to Favorites</p>
      </button>
    );
  }

  let content
  if (options.length == 0 && !flag) {
    content = bakeryData.map(item => (
        <div className="Menu">
          <BakeryItem item={item}></BakeryItem>
          <MyButton item={item} onClick={myFunction}/>
        </div>
      ))
  }


  // console.log(options)
  return (
    <div className="App">
      <div className="Header">
        <h1>The Walking Bakery</h1>{" "}
      </div>
      <div className="Cart">
        <div className="CartHeader">
            <h2>Filter Options</h2>
             {/* <p className="Total"></p> */}
          </div>
        <h4>Sort By</h4>
          <Button name="Popularity" onClick={mySort}/>
          <Button name="Price" onClick={mySort}/>
          <Button name="Calories" onClick={mySort}/>
        <h4>Type</h4>
          <label>
            <input
          type="checkbox"
        />
        <ButtonType name="Pastry" onClick={myfilter}/>
      </label>
          <ButtonType name="Pastry" onClick={myfilter}/>
          <ButtonType name="Bread" onClick={myfilter}/>
          <ButtonType name="Cake" onClick={myfilter}/>
        <h4>Dietary</h4>
          <ButtonDiet name="Gluten-Free" onClick={myfilterDiet}/>
          <ButtonDiet name="Dairy-free" onClick={myfilterDiet}/>
          <ButtonDiet name="Nut-Free" onClick={myfilterDiet}/>
        <h4>Other</h4>
          <ButtonFavs name="Favorite" onClick={myFavorites}/>
      </div>
      {/* <h1>The Bakery</h1> */}
      {/* {bakeryData.map((item, index) => {
        <div>
          <BakeryItem item={item}></BakeryItem>
          <MyButton item={item} onClick={myFunction}/>
        </div>
      })} */}
      {content}

      {options.map((item, index) => (
        <div className="Menu">
          <BakeryItem item={item}></BakeryItem>
          <ButtonFav item={item} onClick={myFunctionAdd}/>
        </div>
      ))}
    </div>
  );
}

export default App;
